import React, { Component } from 'react';
import { Nav, NavDropdown } from 'react-bootstrap';
import '../styles/CategoryMenu.css';
import { Link } from 'react-router-dom';
class CatagoryMenu extends Component {

	constructor(props) {
		super(props);
		this.state = {
			nav1: false,
			nav2: false,
			nav3: false,
			nav4: false,
			nav5: false,
			nav6: false,
			nav7: false,
		};
	}

	render() {
		return (
			<Nav justify variant="tabs" defaultActiveKey="/home" as="ul" >

				<NavDropdown title="Clothing" id="nav-dropdown"
					show={this.state.nav1}
					onMouseEnter={() => this.setState({ nav1: true })}
					onMouseLeave={() => this.setState({ nav1: false })}
				>
					<NavDropdown.Item className="itemSize" eventKey="1.1">
						{this.getCategoryLink('Clothing', 'Top')}
					</NavDropdown.Item>
					<NavDropdown.Item eventKey="1.2">
						{this.getCategoryLink('Clothing', 'Bottom')}
					</NavDropdown.Item>
					<NavDropdown.Item eventKey="1.3">
						{this.getCategoryLink('Clothing', 'Dress')}
					</NavDropdown.Item>
				</NavDropdown>

				<NavDropdown title="SportWear" id="nav-dropdown"
					show={this.state.nav2}
					onMouseEnter={() => this.setState({ nav2: true })}
					onMouseLeave={() => this.setState({ nav2: false })} >
					<NavDropdown.Item className="itemSize" eventKey="2.1">
					{this.getCategoryLink('SportWear', 'Excercis')}
						
					</NavDropdown.Item>
					<NavDropdown.Item eventKey="2.2">
					{this.getCategoryLink('SportWear', 'Shoes')}
						Shoes
					</NavDropdown.Item>
					<NavDropdown.Item eventKey="3.3">
					{this.getCategoryLink('SportWear', 'Flixable')}
						
					</NavDropdown.Item>
				</NavDropdown>

				<NavDropdown title="Outerwear" id="nav-dropdown"
					show={this.state.nav3}
					onMouseEnter={() => this.setState({ nav3: true })}
					onMouseLeave={() => this.setState({ nav3: false })} >
					<NavDropdown.Item className="itemSize" eventKey="3.1">
					{this.getCategoryLink('Outerwear', 'Jacket')}
						
					</NavDropdown.Item>
					<NavDropdown.Item eventKey="3.2">
					{this.getCategoryLink('Outerwear', 'Sweater')}
						
					</NavDropdown.Item>
					<NavDropdown.Item eventKey="3.3">
					{this.getCategoryLink('Outerwear', 'Coat')}
						
					</NavDropdown.Item>
				</NavDropdown>

				<NavDropdown title="Bags" id="nav-dropdown"
					show={this.state.nav4}
					onMouseEnter={() => this.setState({ nav4: true })}
					onMouseLeave={() => this.setState({ nav4: false })} >
					<NavDropdown.Item className="itemSize" eventKey="4.1">
					{this.getCategoryLink('SportWear', 'Hand Bag')}
						
					</NavDropdown.Item>
					<NavDropdown.Item eventKey="4.2">
					{this.getCategoryLink('SportWear', 'Shoulder Bag')}
						
					</NavDropdown.Item>
					<NavDropdown.Item eventKey="4.3">
					{this.getCategoryLink('SportWear', 'Backpack')}
						
					</NavDropdown.Item>
				</NavDropdown>

				<NavDropdown title="Shoses" id="nav-dropdown" show={this.state.nav5}
					onMouseEnter={() => this.setState({ nav5: true })}
					onMouseLeave={() => this.setState({ nav5: false })} >
					<NavDropdown.Item className="itemSize" eventKey="5.1">
					{this.getCategoryLink('SportWear', 'Sport Shose')}
						
					</NavDropdown.Item>
					<NavDropdown.Item eventKey="5.2">
					{this.getCategoryLink('SportWear', 'Boots')}
						
					</NavDropdown.Item>
					<NavDropdown.Item eventKey="5.3">
					{this.getCategoryLink('SportWear', 'Casual Shoes')}
						
					</NavDropdown.Item>
				</NavDropdown>

				<NavDropdown title="Beauty" id="nav-dropdown"
					show={this.state.nav6}
					onMouseEnter={() => this.setState({ nav6: true })}
					onMouseLeave={() => this.setState({ nav6: false })} >
					<NavDropdown.Item className="itemSize" eventKey="6.1">
					{this.getCategoryLink('SportWear', 'Makeup')}
						
					</NavDropdown.Item>
					<NavDropdown.Item eventKey="6.2">
					{this.getCategoryLink('SportWear', 'Hair Care')}
						
					</NavDropdown.Item>
					<NavDropdown.Item eventKey="6.3">
					{this.getCategoryLink('SportWear', 'Skin Care')}
						
					</NavDropdown.Item>
				</NavDropdown>

				<NavDropdown title="Accessories" id="nav-dropdown" 
				show={this.state.nav7}
					onMouseEnter={() => this.setState({ nav7: true })}
					onMouseLeave={() => this.setState({ nav7: false })} >
					<NavDropdown.Item className="itemSize" eventKey="7.1">
					{this.getCategoryLink('SportWear', 'Belts')}
						
					</NavDropdown.Item>
					<NavDropdown.Item eventKey="7.2">
					{this.getCategoryLink('SportWear', 'Glasses')}
						
						</NavDropdown.Item>
					<NavDropdown.Item eventKey="7.3">
					{this.getCategoryLink('SportWear', 'Neckles')}
						
					</NavDropdown.Item>
				</NavDropdown>
			</Nav >
		);
	}

	getCategoryLink(mainCategory, subCategory) {
		return <Link to={{
			pathname: '/products',
			state: {
				category: {
					main: 'Clothing',
					sub: 'Bottom'
				}
			}
		}}>
			Bottoms</Link>;
	}
}

export default CatagoryMenu;
