import React from 'react';
import { FormControl, InputGroup,Button } from 'react-bootstrap';

const goToProducts = () => this.props.history.push('/products')

const SearchBox = ({ onSearchChange, onRequestSearch}) => {
	return (
		<InputGroup className="mb-6" inline >
			<FormControl
				placeholder="What are you looking for?"
				onChange={onSearchChange}
			/>
			<InputGroup.Append>
				<Button variant="outline-secondary" onClick={e =>{onRequestSearch(); goToProducts()}}>Search</Button>
			</InputGroup.Append>
		</InputGroup>
	)
}

export default SearchBox;
