import React, { Component } from 'react';
import { Form, Container, Col, Button } from 'react-bootstrap';
import EmailIcon from '@material-ui/icons/Email';

import '../styles/register.css';
class Register extends Component {

	constructor(props) {
		super(props);
		this.state = {
			username: '',
			first_name: '',
			last_name: '',
			password: '',
			email: '',
			date_birth: null,
			errorMessage: '',
			confirm: false,
			ready: false,
		}
	}


	onUsernameChange = (event) => {
		this.setState({ username: event.target.value });
		this.isFormValidate();
	}
	onFirstNameChange = (event) => {
		this.setState({ first_name: event.target.value });
		this.isFormValidate();
	}
	onLastNameChange = (event) => {
		this.setState({ last_name: event.target.value });
		this.isFormValidate();
	}
	onEmailChange = (event) => {
		this.setState({ email: event.target.value });
		this.isFormValidate();
	}
	onDateBirthChange = (event) => {
		this.setState({ date_birth: event.target.value })
		this.isFormValidate();
	}
	onDateBirthChange = (event) => {
		console.log(typeof(event.target.value));
		this.setState({ date_birth: event.target.value })
		console.log(typeof(this.state.date_birth));
		this.isFormValidate();
	}
	onPasswordChange = (event) => {
		this.setState({ password: event.target.value });
		this.isFormValidate();
	}
	onPasswordConfirmChange = (event) => {
		let confirmPass = event.target.value;
		if (this.state.password !== confirmPass) {
			this.setState({
				errorMessage: ' password does not match',
				confirm: false,
			});
		}
		else {
			this.setState({
				errorMessage: '',
				confirm: true,
			});
			this.isFormValidate();
		}
	}

	isFormValidate() {
		const { first_name, last_name, username, email, password, confirm, date_birth } = this.state;
		// console.log(this.state);
		if (this.isEmpty(username) || this.isEmpty(first_name) || this.isEmpty(last_name) || this.isEmpty(password) || this.isEmpty(email) || this.isEmpty(date_birth) || !confirm) {
			this.setState({ ready: false });
		}
		else {
			this.setState({ ready: true });
		}
	}

	isEmpty(text) {
		if (text === '' || text.trim() === '') {
			return true;
		}
	}

	onSubmitForm = (event) => {
		event.preventDefault();
		console.log(this.state.email);
		fetch("http://localhost:3000/register", {
			method: 'post',
			header: { 'Content-Type': 'application/json' },
			body: JSON.stringify({
				username: this.state.username,
				first_name: this.state.first_name,
				last_name: this.state.last_name,
				email: this.state.email,
				date_birth: this.state.date_birth,
				joined: this.state.joined,
			})
		}).then(response => response.json())
			.then(user => {
				if (user) {
					this.props.setUser(user);
					console.log(user);
				}
			})
	}


	render() {

		return (
			<Container className=" div container">
				<Col lg={6} md={8} className="col">

					<h3 className="h3">Register</h3>
					<hr className="line" />

					<Form className="AnonymousPro-font">
						<Form.Group className="name">
							<Form.Label>First Name</Form.Label>
							<Form.Control type="text" onChange={this.onFirstNameChange} required />
						</Form.Group>
						<Form.Group className="name lastname">
							<Form.Label>Last Name</Form.Label>
							<Form.Control type="text" onChange={this.onLastNameChange} />
						</Form.Group>
						<Form.Group>
							<Form.Label >Username</Form.Label>
							<Form.Control type="text" onChange={this.onUsernameChange} />
							<Form.Text>The username must unique.</Form.Text>
						</Form.Group>
						<Form.Group>
							<Form.Label>Email</Form.Label>
							<EmailIcon className="icon" color="secondary" />
							<Form.Control type="email" onChange={this.onEmailChange} />
						</Form.Group>
						<Form.Group>
							<Form.Label>Password</Form.Label>
							<Form.Control type="password" onChange={this.onPasswordChange} />
							<Form.Text>{this.state.password}</Form.Text>
						</Form.Group>
						<Form.Group>
							<Form.Label>Confirm password</Form.Label>
							<Form.Control type="password" onChange={this.onPasswordConfirmChange} required />
							<Form.Text>{this.state.errorMessage}</Form.Text>
						</Form.Group>
						<Form.Group>
							<Form.Label>Date of birth</Form.Label>
							<Form.Control type="date" onChange={this.onDateBirthChange}/>
						</Form.Group>
						<Form.Group id="formGridCheckbox">
							<Form.Check type="checkbox" label="Accept terms and conditions" />
						</Form.Group>

						<Button className="btn" variant="primary" type="submit" onClick={this.onSubmitForm} disabled={!this.state.ready}>
							Submit
  						</Button>
					</Form>
				</Col>
			</Container>

		);
	}


}

export default Register;
