import React, { Component } from 'react'
import { Redirect } from 'react-router-dom';


class Search extends Component {

	constructor(props) {
		super(props);
		this.state = {
			main: '',
			sub: '',
			items: [],
			error: '',
		}
	}

	componentDidMount() {
		this.getItems();

	}

	getItems() {
		fetch('http://localhost:3000/products/search?q=' + this.props.location.state.keyword, {
			method: 'get',
			headers: { 'Content-Type': 'application/json' },
		}).then(response => response.json())
			.then(it => {
				if (it[0].id) {
					this.setState({ items: it, error: '', })
					console.log(it);
					return it;
				}
				else if (it[0].error) {
					this.setState({ error: it[0].error, items: [] })
					return it;
				}
			})
			
	}

	render() {
		console.log(this.state);
		return (
			<Redirect
				to={{
					pathname: "/products",
					state: { items: this.state.items }
				}}
			/>

		);
	}

}


export default Search;
