import React, { Component } from 'react';
import data from '../../data.json';
import { Container, Row, Col, Carousel, Button } from 'react-bootstrap';
import '../styles/ProductDetails.css';

class ProductDetails extends Component {

	constructor(props) {
		super(props);
		this.state = {
			item: '',
		};
	}
	//TODO: replcase this with one that search the database
	componentDidMount() {
		data.forEach(element => {
			var currentId = element.id.toString();
			if (currentId === this.props.match.params.id) {
				this.setState({ item: element })
			}
		});
	}

	render() {
		const item = this.state.item;
		return (
			<Container className="justify-content-md-center">
				<Row >
					<h2>{item.name}</h2>
				</Row>
				<Row >
					<Col xs={6} md={4} lg={6}>
						<Carousel>
							<Carousel.Item>
								<img className="d-block w-100" src={item.image}
									alt="First slide" />
							</Carousel.Item>
							<Carousel.Item>
								<img className="d-block w-100" src={item.image}
									alt="second slide" />
							</Carousel.Item>
							<Carousel.Item>
								<img className="d-block w-100" src={item.image}
									alt="Third slide" />
							</Carousel.Item>
						</Carousel>
					</Col>
					<Col xs={6} md={4} >
						<Row >
							<p >Sizes </p>
							<div class="pdp-waist-size_info clearfix">






								<a class="pdp-size-swatch " title="MEDIUM" data-size="MEDIUM" href="javascript:void(0);">MEDIUM</a>



								<a class="pdp-size-swatch " title="SMALL" data-size="SMALL" href="javascript:void(0);">SMALL</a><a class="pdp-size-swatch " title="LARGE" data-size="LARGE" href="javascript:void(0);">LARGE</a>



								<a class="pdp-size-swatch " title="X LARGE" data-size="X LARGE" href="javascript:void(0);">X LARGE</a>



								<a class="pdp-size-swatch " title="XX LARGE" data-size="XX LARGE" href="javascript:void(0);">XX LARGE</a>


							</div>
						</Row>
						<Row >
							<ul>
								<li style={{ fontSize: 20 }}>Price</li>
								<li style={{ fontSize: 26, color: 'blue' }}>${item.price}</li>
								<li style={{ fontSize: 14, color: 'red' }}>Original $ {item.price + 10}</li>
							</ul>
						</Row>
						<Row>
							<Button className="addToCart" variant="success">Add to Cart</Button>
						</Row>
					</Col>
				</Row>
				<Row className="desc">
					<div style={{ fontSize: '1.2em', fontWeight: 'bold' }}>{item.longTitle}</div>
					{item.desc}
				</Row>
			</Container>
		)
	}
}

export default ProductDetails;
