import React, {Component} from 'react';

class ProductDetails extends Component {

	constructor(props){
		super(props);
		this.state({
			id:'',
		})
	}
	componentDidMount(){
		this.setState({id:this.props.match.params})
	}
	render(){
		return(
			<div>
				The ID : {this.state.id}
			</div>
		)
	}
}

export default ProductDetails;
