import React from 'react';
import { NavDropdown } from 'react-bootstrap';


const CategoryMenuTab = ({navState, navNum, setNavState, main, sub = []}) => {
	return <NavDropdown title={main}
		id="nav-dropdown"
		show={navState}
		onMouseEnter={() => setNavState(navNum, true)}
		onMouseLeave={() => setNavState(navNum, false)}>

		{
			sub.map(subC =>
				<NavDropdown.Item
					className="itemSize"
					eventKey={subC}
					onClick={() => this.getItems(main, subC)}>
					{subC}
				</NavDropdown.Item>
			)
		}
	</NavDropdown>
}


export default CategoryMenuTab;
