import * as CONSTANTS from './Constants.js';

export const setSearchField = (text) => ({
	type: CONSTANTS.CHANGE_SEARCH_FIELD,
	payload: text
})



export const requestSearchItems = (dispatch, keyword) => {
	dispatch({
		type: CONSTANTS.REQUEST_ITEMS_PENDING,
	});

	fetch('http://localhost:3000/products/search?q=' + keyword, {
		method: 'get',
		headers: { 'Content-Type': 'application/json' },
	}).then(response => response.json())
		.then(data => {
			if (data[0].id) {
				dispatch({
					type: CONSTANTS.REQUEST_ITEMS_SUCCESS,
					payload: data
				})

				return (data);
			}
			else if (data[0].error) {
				this.setState({ error: data[0].error, items: [] })
			}
		})


}
