import React, { Component } from 'react'
import { Redirect, Link } from 'react-router-dom';


class Search extends Component {

	constructor(props) {
		super(props);
		this.state = {
			main: '',
			sub: '',
			items: [],
			error: '',
		}
	}

	componentDidMount() {
		this.getItems();
	}

	getItems = () => {
		fetch('http://localhost:3000/products/search?q=' + this.props.location.state.keyword, {
			method: 'get',
			headers: { 'Content-Type': 'application/json' },
		}).then(response => response.json())
			.then(it => {
				if (it[0].id) {
					this.setState({ items: it, error: '', })
					
					return(it);
				}
				else if (it[0].error) {
					this.setState({ error: it[0].error, items: [] })
				}
			})
			
	}

	render() {
		return (
			<div style={{
				color: 'blue',
				textAlign: 'center'
			}}>
			{this.state.items.map(item => item)}
			{/* <Redirect
				to={{
					pathname: "/products",
					state: { items: this.getItems() }
				}}
			/> */}

			{/* <Link
				to={{
					pathname: "/products",
					state: { items: this.state.items }
				}}
			> Move </Link> */}
			</div>

		);
	}

}


export default Search;
