import React, { Component } from 'react';
import { Form, Container, Col, Button } from 'react-bootstrap';
import EmailIcon from '@material-ui/icons/Email';
import { connect } from 'react-redux';
import '../styles/register.css';

class Login extends Component {

	constructor(props) {
		super(props);
		this.state = {
			password: '',
			email: '',
			errorMessage: '',
			confirm: false,
			resError:'',
		}
	}
	
	onEmailChange = (event) => {
		this.setState({ email: event.target.value });
	}
	
	onPasswordChange = (event) => {
		this.setState({ password: event.target.value });
	}
	
	isFormValidate() {
		const { email, password } = this.state;

		if  (this.isEmpty(password) || this.isEmpty(email)) {
			return false
		}
		else {
			return true;
		}
	}

	isEmpty(text) {
		if (text.trim() === '') {
			return true;
		}
	}
	
	render() {
		return (
			
			<Container className=" div container">
			<div className="errorMessage">{this.state.errorMessage}</div>
				<Col lg={6} md={8} className="col_register">
					<h3 className="h3">Login</h3>
					<hr className="line" />
					<Form className="AnonymousPro-font">
						<Form.Group>
							<Form.Label>Email</Form.Label>
							<EmailIcon className="icon" color="secondary" />
							<Form.Control type="email" required onChange={this.onEmailChange} />
						</Form.Group>
						<Form.Group>
							<Form.Label>Password</Form.Label>
							<Form.Control type="password" required onChange={this.onPasswordChange} onBlur={this.enableConfirmPass} />
						</Form.Group>
						
						<Button className="btnSubmit" 
						variant="primary" type="submit" 
						onClick={this.onSubmitForm}
						
						>
							Submit
  						</Button>
					</Form>
				</Col>
			</Container>
			
		);
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(Login);
