import React, { Component } from 'react'
import { Nav, Navbar, Form, Button, FormControl } from 'react-bootstrap';
import '../styles/NavBar.css';


class NavBar extends Component {


    constructor(props) {
      super(props);
      this.state = {
        keyword: '',
      }
  }

  onChangeKeyword = (event) => {
    this.setState({ keyword: event.target.value });
    // console.log(this.state.keyword);
  }

  getItems() {
    const params = new URLSearchParams();
    
    console.log(this.state.keyword);
    params.append('keyword',this.state.keyword);

    fetch('http://localhost:3000/products/search', {
      method: 'get',
      headers: { 'Content-Type': 'application/json' },
      search: params.toString(),
    }).then(response => response.json())
      .then(it => {
        if (it[0].id) {
          this.setState({ items: it, error: '', })
        }
        else if (it[0].error) {
          this.setState({ error: it[0].error, items: [] })
        }

      })
  }


  render() {
    return (
      <Navbar className="navbar navbar-expand-lg navbar-light bg-light navbar-dark bg-custom" bg="light" expand="lg">
        <Navbar.Brand href="/">LOGO</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto">
            <Nav.Link className="link" href="/">
              Home
            </Nav.Link>
          </Nav>
          <Form inline style={{ width: '50%' }}>
            <FormControl type="text" placeholder="Search" className="mr-sm-2" style={{ width: '75%' }} onChange={this.onChangeKeyword} />
            <Button variant="outline-success" onClick={this.getItems}>Search</Button>
          </Form>


          {
            this.props.username !== '' ?
              <ul ul className="navbar-nav ml-auto">
                <li className="nav-item active">
                  Welcome {this.props.username}
                </li>
              </ul> :
              <ul className="navbar-nav ml-auto">
                <Nav.Link className="link" href="/login">
                  Login
                  </Nav.Link>
                <Nav.Link className="link" href="/register">
                  Register
                </Nav.Link>
              </ul>
          }
        </Navbar.Collapse>
      </Navbar >
    );
  }
}

export default NavBar;
