import React, { Component } from 'react';
import data from '../../data.json';
import { Container, Row, Col, Image } from 'react-bootstrap';
import '../styles/ProductDetails.css';

class ProductDetails extends Component {

	constructor(props) {
		super(props);
		this.state = {
			item: '',
		};
	}
	//TODO: replcase this with one that search the database
	componentDidMount() {
		data.forEach(element => {
			var currentId = element.id.toString();
			if (currentId === this.props.match.params.id) {
				this.setState({ item: element })
			}
		});
	}

	render() {
		const item = this.state.item;
		return (
			<Container className="justify-content-md-center">
				<Row className="border">
					<h2>{item.name}</h2>
				</Row>
				<Row className="border blue ">
					<Col className="border" xs={6} md={4}>
						<Image src={item.image} className="img" rounded />
					</Col>
					<Col xs={6} md={4} className="border">
						<Row className="border">
							<p  className="rightCol">Sizes </p>
						</Row>
						<Row className="border">
							<p className="rightCol">Price {item.price}</p>
						</Row>
					</Col>
				</Row>
				<Row className="border">
					The ID : {item.desc}
				</Row>
			</Container>
		)
	}
}

export default ProductDetails;
