import {createStore, combineReducers, applyMiddleware} from 'redux';
import thunkMiddleware from 'redux-thunk';
import {searchItems} from './Reducers.js';

// const rootReducers = combineReducers({searchItems});
export const Store = createStore(searchItems, applyMiddleware(thunkMiddleware));
