import React, { Component } from 'react'
import ProductCard from './ProductCard.js';
import Error from './Error.js';

class Search extends Component {

	constructor(props) {
		super(props);
		this.state = {
			main: '',
			sub: '',
			items: [],
			error: '',
		}
	}

	componentDidMount() {
		this.getItems();
	}

	getItems = () => {
		fetch('http://localhost:3000/products/search?q=' + this.props.location.state.keyword, {
			method: 'get',
			headers: { 'Content-Type': 'application/json' },
		}).then(response => response.json())
			.then(it => {
				if (it[0].id) {
					this.setState({ items: it, error: '', })

					return (it);
				}
				else if (it[0].error) {
					this.setState({ error: it[0].error, items: [] })
				}
			})
	}

	render() {
		return (
			<div className="container-grid" style={{ marginTop: '70px' }} >
				{
					this.props.location.state.items !== undefined
						? this.state.items.map(item => (
							<ProductCard key={item.id} item={item} />))
						: <Error msg={'No item found'} />
				}
			</div >
		);
	}

}


export default Search;
