import React from 'react';
import ProductCard from './ProductCard.js';
import '../styles/ProductCard.css';


class CardsList extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			main: '',
			sub: '',
			items: [],
		}
	}


	componentDidMount() {
		this.updateState();
	}

	componentDidUpdate(){
		this.updateState();
	}

	updateState() {
		if (this.state.sub !== this.props.location.state.sub) {
			this.setState({
				main: this.props.location.state.main,
				sub: this.props.location.state.sub,
			})
		}
		this.getItems(this.state.main, this.state.sub)
	}

	getItems(main , sub) {
		fetch('http://localhost:3000/products/category', {
			method: 'post',
			headers: { 'Content-Type': 'application/json' },
			body: JSON.stringify({
				main: main,
				sub: sub,
			})
		}).then(response => console.log(response.json()))

	}

	render() {
		;
		return (
			<div className="container-grid" style={{ marginTop: '70px' }} >
				{
					<div style={{ color: 'blue' }}>{this.state.main}-- {this.state.sub}
					{/* {this.getItems(this.state.main, this.state.sub)} */}
					</div>
					// this.state.items && this.state.items !== null && this.items !== undefined
					// 	? this.state.items.map(item => (
					// 		<ProductCard key={item.id} item={item} />))
					// 	: this.state.items === 'No results found'
					// 		? <div style={{ color: 'blue' }}>ERROR: data not found {this.state.main}-- {this.state.sub}</div>
					// 		: <div style={{ color: 'blue' }}>ERROR</div>
				}
			</div >
		)
	}
}


export default CardsList;
