import React, { Component } from 'react';
import { FormControl, InputGroup, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { withRouter } from 'react-router-dom';

class SearchBox extends Component {

	doIt = () => {
		this.props.onRequestSearch(this.props.searchField);
		this.props.history.push('/products/search');
	}

	render() {
		return (
			<InputGroup className="mb-6" inline >
				<FormControl
					placeholder="What are you looking for?"
					onChange={this.props.onSearchChange}
				/>
				<InputGroup.Append>
					{/* <Link variant="outline-secondary" to='/products/search' >Search</Link> */}
					<Button variant="outline-secondary" onClick={doIt}>Search</Button>
				</InputGroup.Append>
			</InputGroup>
		)
	}
}
export default SearchBox;
