import React, { Component } from 'react';
import data from '../../data.json';
import { Container, Row, Col, Carousel } from 'react-bootstrap';
import '../styles/ProductDetails.css';

class ProductDetails extends Component {

	constructor(props) {
		super(props);
		this.state = {
			item: '',
		};
	}
	//TODO: replcase this with one that search the database
	componentDidMount() {
		data.forEach(element => {
			var currentId = element.id.toString();
			if (currentId === this.props.match.params.id) {
				this.setState({ item: element })
			}
		});
	}

	render() {
		const item = this.state.item;
		return (
			<Container className="justify-content-md-center">
				<Row className="border">
					<h2>{item.name}</h2>
				</Row>
				<Row className="border">
					<Col className="border" xs={6} md={4}>
						<Carousel>
							<Carousel.Item>
								<img className="d-block w-100" src={item.image}
									alt="First slide" />
							</Carousel.Item>
							<Carousel.Item>
								<img className="d-block w-100" src={item.image}
									alt="second slide" />
							</Carousel.Item>
							<Carousel.Item>
								<img className="d-block w-100" src={item.image}
									alt="Third slide" />
							</Carousel.Item>
						</Carousel>
					</Col>
					<Col xs={6} md={4} className="border">
						<Row className="border">
							<p >Sizes </p>
						</Row>
						<Row className="border">
							<ul>
								<li>Price</li>
								<li style={{fontSize: '55'}}>{item.price}</li>
							</ul>
						</Row>
					</Col>
				</Row>
				<Row className="border">
					<p>{item.desc}</p>
				</Row>
			</Container>
		)
	}
}

export default ProductDetails;
