import {CHANGE_SEARCH_FIELD} from './Constants.js';

const intialSearchState = {
	searchField: ''
}

export const searchItems = (state=intialSearchState, action = {}) => {
	console.log(action.type.CHANGE_SEARCH_FIELD);
	switch (action.type) {
		case CHANGE_SEARCH_FIELD:
			return { ...state, searchField: action.payload };
		default:
			return state;
	}
}


