import * as CONSTANTS from './Constants.js';

const initialStateSearch = {
	searchField: ''
  }
  
  export const searchItems = (state=initialStateSearch, action={}) => {
	switch (action.type) {
	  case CONSTANTS.CHANGE_SEARCH_FIELD:
		return {...state, searchField: action.payload};
	  default:
		return state
	}
  }

const intialItemsState = {
	Items: [],
	isPending: true
}

export const requestItems = (state=intialItemsState, action={}) => {
	switch (action.type) {
		case CONSTANTS.REQUEST_ITEMS_PENDING:
			return
		default:
			break;
	}
}
