import React, { Component } from 'react';
import { Nav, Navbar } from 'react-bootstrap';
import SearchBox from './SearchBox.js';

import '../styles/NavBar.css';

import { setSearchFieldAction, requestSearchItemsAction } from '../Actions.js';
import { connect } from 'react-redux';


const mapStateToProps = (state) => {
  return {
    searchField: state.searchItemsReducer.searchField,
    items: state.requestSearchItemsReducer.items,
    error: state.requestSearchItemsReducer.error,
    isPending: state.requestSearchItemsReducer.isPending
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    onSearchChange: (event) => dispatch(setSearchFieldAction(event.target.value)),
    onRequestSearch: (keyword) => dispatch(requestSearchItemsAction(keyword))
  }
}

class NavBar extends Component {

  constructor(props) {
    super(props);
  }

  render() {
    return (
      <Navbar className="navbar navbar-expand-lg navbar-light bg-light navbar-dark bg-custom" bg="light" expand="lg">
        <Navbar.Brand href="/">LOGO</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto">
            <Nav.Link className="link" href="/">
              Home
            </Nav.Link>
          </Nav>

          <InputGroup className="mb-6" inline >
            <FormControl
              placeholder="What are you looking for?"
              onChange={this.props.onSearchChange}
            />
            <InputGroup.Append>
              <Button variant="outline-secondary" onClick={() => this.props.onRequestSearch(this.props.searchField)}>Search</Button>
            </InputGroup.Append>
          </InputGroup>
          {
            this.props.username !== '' ?
              <ul ul className="navbar-nav ml-auto">
                <li className="nav-item active">
                  Welcome {this.props.username}
                </li>
              </ul> :
              <ul className="navbar-nav ml-auto">
                <Nav.Link className="link" href="/login">
                  Login
                  </Nav.Link>
                <Nav.Link className="link" href="/register">
                  Register
                </Nav.Link>
              </ul>
          }
        </Navbar.Collapse>
      </Navbar >
    );
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(NavBar);
