import * as Constants from './Constants.js';

const intialSearchState = {
	searchField: ''
}

export const searchItems = (state = intialSearchState, action = {}) => {
	switch (action.type) {
		case Constants.CHANGE_SEARCH_FIELD:
			return { ...state, searchField: action.payload };
		default:
			return state;
	}
}


