import React, { Component } from 'react';
import data from '../../data.json';

class ProductDetails extends Component {

	constructor(props) {
		super(props);
		this.state = {
			item: '',
		};
	}
	componentDidMount() {
		this.setState({ id: this.props.match.params })
	}

	matchedItem(id) {
		data.forEach(item => {
			console.log(typeof(item.id) === typeof(id));
			if (item.id === id){
				this.setState({item: item});
				
			}
		});
	}

	render() {
		return (
			<div>
				Item page
				{/* {console.log(this.props.match.params.id)} */}
				{this.matchedItem(this.props.match.params.id)}
				{/* {console.log(data)} */}
				{console.log(this.state.item)}
				The ID : {this.props.match.params.id}
			</div>
		)
	}
}

export default ProductDetails;
