import React from 'react';
import ProductCard from './ProductCard.js';
import '../styles/ProductCard.css';


class CardsList extends React.Component {
	constructor() {
		super();
		this.state = {
			items: null,
		}
	}

	componentDidMount(){
		this.fetchDB();
	}


	fetchDB() {
		const params = this.props.location.state
		let target = '';
		params.category !== "all" ?
			target = 'products/category'
			: target = 'products';

		fetch(`http://localhost:3000/${target}`, {
			method: 'POST',
			headers: { 'Content-Type': 'application/json' },
			body: JSON.stringify(params),
		}).then(response => response.json()).then(items => this.setState({ items: items }))
	}

	render() {
		this.fetchDB();
		// console.log(this.state.items);
		return (
			<div className="container-grid" style={{ marginTop: '70px' }}>
				{
					this.state.items && this.state.items !== null && this.items !== undefined
						? this.state.items.map(item => (
							<ProductCard key={item.id} item={item} />))
						: this.state.items === 'No results found'
							? <div style={{ color: 'blue' }}>ERROR: data not found</div>
							: <div style={{ color: 'blue' }}>ERROR</div>
				}
			</div>
		)
	}
}


export default CardsList;
