import React, { Component } from 'react';
import { Nav, Navbar, FormControl, InputGroup, Button } from 'react-bootstrap';
import { connect } from 'react-redux';
import { Redirect } from 'react-router-dom';
import { useHistory } from "react-router-dom";
import { withRouter } from "react-router-dom";
import { setSearchFieldAction, requestSearchItemsAction } from '../Actions.js';
import SearchBox from './SearchBox.js';
import '../styles/NavBar.css';


let history = useHistory();

const mapStateToProps = (state) => {
  return {
    searchField: state.searchItemsReducer.searchField,
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    onSearchChange: (event) => dispatch(setSearchFieldAction(event.target.value)),
    onRequestSearch: (keyword) => dispatch(requestSearchItemsAction(keyword))
  }
}

class NavBar extends Component {

  constructor(props){
    super();
  }
  
  doIt = () => {
    this.props.onRequestSearch(this.props.searchField);
    // this.props.history.push('/products/search')
    history.push('/products/search');
  }

  render() {
    return (
      <Navbar className="navbar navbar-expand-lg navbar-light bg-light navbar-dark bg-custom" bg="light" expand="lg">
        <Navbar.Brand href="/">LOGO</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto">
            <Nav.Link className="link" href="/">
              Home
            </Nav.Link>
          </Nav>

          {/* <SearchBox onSearchChange={this.props.onSearchChange}/> */}

          <InputGroup className="mb-6" inline >
            <FormControl
              placeholder="What are you looking for?"
              onChange={this.props.onSearchChange}
            />
            <InputGroup.Append>
              {/* <Link variant="outline-secondary" to='/products/search' >Search</Link> */}
              <Button variant="outline-secondary" onClick={this.doIt}>Search</Button>
            </InputGroup.Append>
          </InputGroup>
          {
            this.props.username !== '' ?
              <ul ul className="navbar-nav ml-auto">
                <li className="nav-item active">
                  Welcome {this.props.username}
                </li>
              </ul> :
              <ul className="navbar-nav ml-auto">
                <Nav.Link className="link" href="/login">
                  Login
                  </Nav.Link>
                <Nav.Link className="link" href="/register">
                  Register
                </Nav.Link>
              </ul>
          }
        </Navbar.Collapse>
      </Navbar >
    );
  }
}

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(NavBar));
