import React, { Component } from 'react';
import data from '../../data.json';

class ProductDetails extends Component {

	constructor(props) {
		super(props);
		this.state = {
			item: '',
		};
	}
	//TODO: replcase this with one that search the database
	componentDidMount() {
		data.forEach(element => {
			var currentId = element.id.toString();
			if (currentId === this.props.match.params.id) {
				this.setState({ item: element })
			}
		});
	}

	render() {
		return (
			<div>

				<Container>
					<Row>
						<Col xs={6} md={4}>
							<Image src="holder.js/171x180" rounded />
						</Col>
						<Col xs={6} md={4}>
							<Image src="holder.js/171x180" roundedCircle />
						</Col>
						<Col xs={6} md={4}>
							<Image src="holder.js/171x180" thumbnail />
						</Col>
					</Row>
				</Container>
				{console.log(this.state.item.name)}
				The ID : {this.state.item.name}
				The ID : {this.state.item.desc}


			</div>
		)
	}
}

export default ProductDetails;
