import React, { Component } from 'react'
import { Redirect } from 'react-router-dom';


class Search extends Component {

	constructor(props) {
		super(props);
		this.state = {
			main: '',
			sub: '',
			items: [],
			error: '',
		}
	}
	
	componentDidMount() {
		const params = new URLSearchParams();
		params.append('keyword', this.props.location.state.keyword);
		fetch('http://localhost:3000/products/search?q=' + this.state.keyword, {
			method: 'get',
			headers: { 'Content-Type': 'application/json' },

		}).then(response => response.json())
			.then(it => {
				if (it[0].id) {
					this.setState({ items: it, error: '', })
					console.log(it);
				}
				else if (it[0].error) {
					this.setState({ error: it[0].error, items: [] })
					console.log(it);
				}
			})
	}

	render() {
		console.log(this.state.items)
		return (
			<div></div>
			// <Redirect
